import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz'; // It's recommended that you use an App Password create through the Bsky App rather than your own
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const agent = new BskyAgent({ service: SERVICE_URL });
        const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
        await agent.login({ identifier: did, password: APP_PASSWORD });
        const { data } = await agent.getAuthorFeed({
          actor: did,
          filter: 'posts_and_author_threads',
          limit: 1, //You can set the limit of posts here, up to 100.
        });

        console.log('Received data:', data); // Log received data

        // Check if data.feed is an array before mapping
        if (Array.isArray(data.feed)) {
          const postsData = data.feed.map(item => ({
            date: new Date(item.post.record.createdAt).toLocaleString('en-US', {
              timeZone: 'America/New_York', // Adjust to your timezone
              dateStyle: 'long',
              timeStyle: 'long'
            }),
            text: item.post.record.text,
            images: item.post.embed?.images?.map(image => ({
              thumb: image.thumb,
              fullsize: image.fullsize,
              alt: image.alt
            })) || [] // Handle cases where images are not present
          }));
          setPosts(postsData);
        } else {
          console.error('Invalid feed data format:', data.feed);
          throw new Error('Invalid feed data format');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  if (error) {
    return <div className='bsky-home skeet-text'>BlueSky Error: {error}</div>;
  }

  return (
    <section className='bsky-home'>
      <h2>Latest Skeet To Bsky</h2>
      <ul className='latest-skeet'>
        {posts.map((post, index) => (
          <li key={index}>
            <p className='skeet-date'>Posted to BlueSky Social by <a>@reverendcrush.com</a> on <u>{post.date}</u></p>
            <p className='skeet-text'>{post.text}</p>
            <div class>
              {post.images.map((image, i) => (
                <div key={i} className="skeet-images"><img className='skeet-img-file' key={i} src={image.thumb} alt={image.alt} /></div>
              ))}
            </div>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default BlueskySocial;
