import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz'; // Replace with your actual app password
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const agent = new BskyAgent({ service: SERVICE_URL });
        const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
        await agent.login({ identifier: did, password: APP_PASSWORD });
        const { data } = await agent.getAuthorFeed({
          actor: did,
          filter: 'posts_and_author_threads',
          limit: 1,
        });

        // Check if data.feed is an array before mapping
        if (Array.isArray(data.feed)) {
          const postsData = data.feed.map(item => {
            const createdAt = item.record?.createdAt;
            const text = item.record?.text;
            return { date: createdAt, text: text };
          });
          setPosts(postsData);
        } else {
          console.error('Invalid feed data format:', data.feed);
          throw new Error('Invalid feed data format');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Latest Posts</h1>
      <ul>
        {posts.map((post, index) => (
          <li key={index}>
            <p>Date: {post.date}</p>
            <p>Text: {post.text}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlueskySocial;
