import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';
import YouTube from 'react-youtube';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz';
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const agent = new BskyAgent({ service: SERVICE_URL });
        const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
        await agent.login({ identifier: did, password: APP_PASSWORD });
        const { data } = await agent.getAuthorFeed({
          actor: did,
          filter: 'posts_and_author_threads',
          limit: 1,
        });
        
        if (Array.isArray(data.feed)) {
          const postsData = data.feed.map(item => {
            const post = item.post;
            const { likeCount, replyCount, repostCount } = post;
            const bangerScore = likeCount * 200 + replyCount * 300 + repostCount * 1000;
            const images = post.embed?.media?.images || [];
            console.log(post)
            return {
              author: post.author,
              date: new Date(post.record.createdAt).toLocaleString('en-US', { timeZone: 'America/New_York', dateStyle: 'long', timeStyle: 'long' }),
              text: post.record.text,
              embed: post.embed,
              bangerScore: bangerScore,
              images: images
            };
          });
          setPosts(postsData);
        } else {
          console.error('Invalid feed data format:', data.feed);
          throw new Error('Invalid feed data format');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  if (error) {
    return <div className='bsky-home skeet-text'>BlueSky Error: {error}</div>;
  }

  return (
    <section className='bsky-home'>
      <h2>Latest Skeet On Bsky</h2>
      <ul className='latest-skeet'>
      {posts.map((post, index) => {
  console.log('Post:', post); // Log the structure of the post object
  return (
    <li key={index}>
      <p className='skeet-date'>Posted by <a href={`https://bsky.app/profile/${post.author.handle}`} target="_blank" rel="noreferrer">@{post.author.handle}</a> to <a href='https://bsky.app/' target="_blank" rel="noreferrer">bsky.app</a> on <u>{post.date}</u></p>
      <div className='skeet-text' style={{ whiteSpace: 'pre-line' }}>
        {parseText(post.text)}
        {post.embed && post.embed.external && post.embed.external.uri.includes('youtu.be') && (
          <div className='skeet-youtube'>
            <div className='youtube-container'>
              <YouTube videoId={getPostYoutubeId(post.embed.external.uri)} />
            </div>
            <h3>{post.embed.external.title}</h3>
            <p className='skeet-metatext'>{post.embed.external.description}</p>
          </div>
        )}
      </div>
      
      {/* Render quoted post text, if it exists */}
      {post.embed && post.embed.record && post.embed.record.value && (
      <div className='quote-reskeet-box'>
        <div className='reskeet-user'>
          <img className='reskeet-pfp' src={post.embed.record.author.avatar} alt={post.embed.record.author.handle} />
          <div>Reskeeting...<br /> 
          <a className='reskeet-handle' href={`https://bsky.app/profile/${post.embed.record.author.handle}`} target="_blank" rel="noreferrer">@{post.embed.record.author.handle}</a>
          </div>
        </div>
        <div className='quoted-post-text'>
          {post.embed.record.value.text}
        </div>
        {/* Render quoted post's images if they exist */}
        {post.embed && post.embed.record && post.embed.record.value && post.embed.record.value.embeds &&
          post.embed.record.value.embeds[0].images &&
          post.embed.record.value.embeds[0].images.length > 0 && (
            <div className='skeet-image-group'>
              {post.embed.record.value.embeds[0].images.map((image, index) => (
                <div key={index} className="skeet-image">
                  {/* Directly using the fullsize URL */}
                  <img className='skeet-img-file' src={image.fullsize} alt={image.alt || 'Quoted post image'} />
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      

      <h3 className='skeet-banger-score'>Banger Score:<br /> <span className='glow'>{post.bangerScore}</span></h3>
    </li>
  );
})}

      </ul>
    </section>
  );
};

const constructImageUrl = (did, blobRef) => {
  return `https://cdn.bsky.app/img/feed_thumbnail/plain/${did}/${blobRef}@jpeg`;
};

const parseText = (text) => {
  if (!text) return ''; // Return an empty string if text is undefined
  const usernameRegex = /@(\w+(?:\.\w+)*)(?=[^\w.@]|$)/g;

  return text.split(usernameRegex).map((part, index) => {
    if (index % 2 === 0) {
      return part; // Normal text
    } else {
      return <a href={`https://bsky.app/profile/${part}`} target="_blank" rel="noreferrer">@{part}</a>; // Link to user profile
    }
  });
};

const getPostYoutubeId = (text) => {
  const matches = text.match(/youtu\.be\/([^?\s]+)/);
  return matches ? matches[1] : null;
};

export default BlueskySocial;
