import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';
import YouTube from 'react-youtube';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz';
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [currentPostIndex, setCurrentPostIndex] = useState(0);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  async function fetchData() {
    try {
      const agent = new BskyAgent({ service: SERVICE_URL });
      const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
      await agent.login({ identifier: did, password: APP_PASSWORD });
      const { data } = await agent.getAuthorFeed({
        actor: did,
        filter: 'posts_and_author_threads',
        limit: 25,
      });

      if (Array.isArray(data.feed)) {
        setPosts(data.feed);
      } else {
        console.error('Invalid feed data format:', data.feed);
        setError('Error fetching data');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Error fetching data');
    }
  }

  const handlePrevious = () => {
    setCurrentPostIndex(prevIndex => Math.max(prevIndex - 1, 0));
  };

  const handleNext = () => {
    setCurrentPostIndex(prevIndex => Math.min(prevIndex + 1, posts.length - 1));
  };

  if (error) {
    return <div className='bsky-home skeet-text'>BlueSky Error: {error}</div>;
  }

  if (posts.length === 0) {
    return <div className='bsky-home skeet-text'>Loading skeets...</div>;
  }

  const currentPost = posts[currentPostIndex];

  if (!currentPost || !currentPost.post || !currentPost.post.record) {
    console.error("Current post is undefined or missing the record property:", currentPost);
    return <div className='bsky-home skeet-text'>Error loading skeet...</div>;
  }

  const parseText = (text) => {
    if (!text) return ''; // Return an empty string if text is undefined
    const usernameRegex = /@(\w+(?:\.\w+)*)(?=[^\w.@]|$)/g;

    return text.split(usernameRegex).map((part, index) => {
      if (index % 2 === 0) {
        return part; // Normal text
      } else {
        return <a href={`https://bsky.app/profile/${part}`} target="_blank" rel="noreferrer">@{part}</a>; // Link to user profile
      }
    });
  };

  const getPostYoutubeId = (url) => {
    let id = '';
    if (url.includes('youtube.com')) {
      const urlParams = new URLSearchParams(new URL(url).search);
      id = urlParams.get('v');
    } else if (url.includes('youtu.be')) {
      id = url.split('youtu.be/')[1];
      id = id.split('?')[0];
    }
    return id;
  };

  return (
    <section className='bsky-home'>
      <h2>Latest Skeet On Bsky</h2>
      <div className='skeet-text'>
        {/* Display the post's author and timestamp */}
        <div className='skeet-header'>
          <img src={currentPost.post.author.avatar} alt={`${currentPost.post.author.name}'s avatar`} className='author-avatar' />
          <p className='skeet-date'>Posted by <a href={`https://bsky.app/profile/${currentPost.post.author.handle}`} target="_blank" rel="noreferrer">@{currentPost.post.author.handle}</a> on <u>{new Date(currentPost.post.record.createdAt).toLocaleString()}</u></p>
        </div>
        <p>{parseText(currentPost.post.record.text)}</p>
        {/* Display images */}
        {currentPost.post.embed?.images?.map((image, index) => (
          <img key={index} src={image.fullsize} alt={image.alt} loading="lazy" className='skeet-img-file' />
        ))}
        {currentPost.post.embed?.external && (
          <div className='webcard-img'>
            <a href={currentPost.post.embed.external.uri} target="_blank" rel="noopener noreferrer">
              <img src={currentPost.post.embed.external.thumb.ref.$link} alt="Website thumbnail" />
              <div className='web-deets'>
                <h3>{currentPost.post.embed.external.title}</h3>
                <p>{currentPost.post.embed.external.description}</p>
              </div>
            </a>
          </div>
        )}
        {currentPost.post.embed?.external?.uri.includes('youtube.com') || currentPost.post.embed?.external?.uri.includes('youtu.be') ? (
          <div className='youtube'>
            <YouTube videoId={getPostYoutubeId(currentPost.post.embed.external.uri)} />
          </div>
        ) : null}
        {/* Render Quote-Reskeets (Quote Reposts) */}
        {currentPost.post.embed && currentPost.post.embed.record && (
          <div className="quote-reskeet-box">
            <div className="quoted-post-header">
              <img src={currentPost.post.embed.record.author.avatar} alt={`${currentPost.post.embed.record.author.name}'s avatar`} className='author-avatar' />
              <p>Reskeeted by <a href={`https://bsky.app/profile/${currentPost.post.embed.record.author.handle}`} target="_blank" rel="noreferrer">@{currentPost.post.embed.record.author.handle}</a></p>
            </div>
            <div className="quoted-post-text">
              {parseText(currentPost.post.embed.record.record.text)}
            </div>
            {/* Render images from the quoted post */}
            {currentPost.post.embed.record.record.embed?.images?.map((image, imgIndex) => (
              <img key={imgIndex} src={image.fullsize} alt={image.alt || 'Quoted post image'} loading="lazy" className='skeet-img-file' />
            ))}
            {/* Render YouTube video from the quoted post */}
            {currentPost.post.embed.record.record.embed?.external?.uri && 
            (currentPost.post.embed.record.record.embed.external.uri.includes('youtube.com') || currentPost.post.embed.record.record.embed.external.uri.includes('youtu.be')) && (
              <div className='youtube'>
                <YouTube videoId={getPostYoutubeId(currentPost.post.embed.record.record.embed.external.uri)} />
              </div>
            )}
          </div>
        )}
      </div>
      <div className="pagination-controls">
        <button onClick={handlePrevious} disabled={currentPostIndex === 0}>Previous</button>
        <button onClick={handleNext} disabled={currentPostIndex >= posts.length - 1}>Next</button>
      </div>
    </section>
  );
};

export default BlueskySocial;
