import React, { useRef, useEffect} from 'react';
import {Link, useLocation} from 'react-router-dom';
import Typed from "typed.js";
import "../styles/Header.css";
import {pushRotate as Menu} from 'react-burger-menu';
import logo from '../img/dudeist_yinyang.png';

export default function Header(){
    const el = useRef(null);
    const typed = useRef(null); // Ref for storing the Typed instance
    let location = useLocation();

    useEffect(() => {
        const header_strings = {
            "/about": ">> REVCRSH_FUN: Fun stuff that the Rev cobbled together.",
            "/projects": ">> REVCRSH_BIO: COMING SOON!",
            "/contact": ">> REVCRSH_CON: Ways to contact His Royal Dudeness other than bsky.",
            "/": ">> REVCRSH_HOM: Hey Duders! Enjoy your visit!",
            "default": ">> //ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R"
        };

        const header_txt = header_strings[location.pathname] || header_strings["default"];

        typed.current = new Typed(el.current, {
            strings: [header_txt],
            startDelay: 300,
            typeSpeed: 5,
            loop: false,
            showCursor: true,
            cursorChar: "_",
        });

        // Cleanup function to destroy Typed instance on component unmount or before creating a new instance
        return () => {
            if (typed.current) {
                typed.current.destroy();
            }
        };
    }, [location.pathname]); // Depend on pathname to re-run animation on route change

    // Function to manually stop Typed animation and perform cleanup
    const killTyped = () => {
        if (typed.current) {
            typed.current.destroy();
        }
    };

    return (
    <div className="header">
        <div className="autotext">
        <span ref={el} className="header_title" id="header-txt" />
        </div>
        <div className="header_nav">
        <Menu right noOverlay width={ '29.5%' } height={ '100%' } pageWrapId={"page-wrap"} outerContainerId={"outer-container"}>
            <img className="nav-logo" src={logo} alt='ReverendCrush.com' />
            <Link className="nav_link" onClick={ killTyped } to='/'>Main </Link>
            <Link className="nav_link" onClick={ killTyped } to='/contact'>Socials</Link>
            <Link className="nav_link" onClick={ killTyped } to='/projects'>For Fun</Link>
            <Link className="nav_link" onClick={ killTyped } to='/about'>Bio</Link>
        </Menu>
        </div>
    </div>      
        );
}
