import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';
import YouTube from 'react-youtube';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz';
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [currentPostIndex, setCurrentPostIndex] = useState(0);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  async function fetchData() {
    try {
      const agent = new BskyAgent({ service: SERVICE_URL });
      const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
      await agent.login({ identifier: did, password: APP_PASSWORD });
      const { data } = await agent.getAuthorFeed({
        actor: did,
        filter: 'posts_and_author_threads',
        limit: 25,
      });

      if (Array.isArray(data.feed)) {
        setPosts(data.feed);
        setCurrentPostIndex(data.feed.length - 1); // Ensures starting on the most current post
      } else {
        console.error('Invalid feed data format:', data.feed);
        setError('Error fetching data');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Error fetching data');
    }
  }

  const handlePrevious = () => {
    setCurrentPostIndex(prevIndex => Math.max(prevIndex - 1, 0));
  };

  const handleNext = () => {
    setCurrentPostIndex(prevIndex => Math.min(prevIndex + 1, posts.length - 1));
  };

  if (error) {
    return <div className='bsky-home skeet-text'>BlueSky Error: {error}</div>;
  }

  if (posts.length === 0) {
    return <div className='bsky-home skeet-text'>Loading skeets...</div>;
  }

  const currentPost = posts[currentPostIndex];

  if (!currentPost || !currentPost.record) {
    return <div className='bsky-home skeet-text'>Loading skeet...</div>;
  }

  return (
    <section className='bsky-home'>
      {console.log(currentPost)}
      <h2>Latest Skeet On Bsky</h2>
      <div className='post skeet-text'>
        <p>{currentPost.record.text}</p>
        {currentPost.embed?.images?.map((image, index) => (
          <img key={index} src={image.fullsize} alt={image.alt} loading="lazy" className='skeet-img-file' />
        ))}
        {currentPost.embed?.external && (
          <div className='webcard-img'>
            <a href={currentPost.embed.external.uri} target="_blank" rel="noopener noreferrer">
              <img src={currentPost.embed.external.thumb.ref.$link} alt="Website thumbnail" />
              <div className='web-deets'>
                <h3>{currentPost.embed.external.title}</h3>
                <p>{currentPost.embed.external.description}</p>
              </div>
            </a>
          </div>
        )}
      </div>
      <div className="navigation-controls">
        <button onClick={handlePrevious} disabled={currentPostIndex === 0}>{"<="}</button>
        <button onClick={handleNext} disabled={currentPostIndex === posts.length - 1}>{"=>"}</button>
      </div>
    </section>
  );
};

export default BlueskySocial;
