import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const DID_URL = "https://bsky.social/xrpc/com.atproto.identity.resolveHandle";
  const APP_PASSWORD = "YOUR_ACTUAL_APP_PASSWORD"; // Replace with your actual app password
  const API_KEY_URL = "https://bsky.social/xrpc/com.atproto.server.createSession";
  const FEED_URL = "https://bsky.social/xrpc/app.bsky.feed.getAuthorFeed";
  const POST_FEED_URL = "https://bsky.social/xrpc/com.atproto.repo.createRecord";

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const handleUrl = encodeURI(`${DID_URL}?handle=${HANDLE}`);
        const handleRep = await axios.get(handleUrl);
        const DID = handleRep.data.did;

        const tokenOpt = {
          headers: { 'Content-Type': 'application/json' },
          data: { "identifier": DID, "password": APP_PASSWORD }
        };
        
        try {
          const tokenRep = await axios.post(API_KEY_URL, tokenOpt);
          const TOKEN = tokenRep.data.accessJwt;

          const feedUrl = encodeURI(`${FEED_URL}?actor=${HANDLE}&limit=2`);
          const feedOpt = {
            headers: { 'Authorization': `Bearer ${TOKEN}` }
          };
          const feedRep = await axios.get(feedUrl, feedOpt);
          const feed = feedRep.data.feed;
          const postsData = feed.map(item => ({
            date: item.post.record.createdAt,
            text: item.post.record.text
          }));
          setPosts(postsData);
        } catch (error) {
          console.error('Error fetching token:', error);
          setError('Error fetching token');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }
    fetchData();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Latest Posts</h1>
      <ul>
        {posts.map((post, index) => (
          <li key={index}>
            <p>Date: {post.date}</p>
            <p>Text: {post.text}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlueskySocial;
