import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz'; // Replace with your actual app password
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        // Create a new BskyAgent instance with the service URL
        const agent = new BskyAgent({ service: SERVICE_URL });

        // Resolve the handle to get the DID
        const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });

        // Authenticate the user with the resolved DID and app password
        await agent.login({ identifier: did, password: APP_PASSWORD });

        // Fetch the author's feed using the resolved DID
        const { data } = await agent.getAuthorFeed({
          actor: did,
          filter: 'posts_and_author_threads',
          limit: 2,
        });

        const { feed: postsArray } = data;
        setPosts(postsArray);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Latest Posts</h1>
      <ul>
        {posts.map((post, index) => (
          <li key={index}>
            <p>Date: {post.record.createdAt}</p> 
            <p>Text: {post.record.text}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlueskySocial;
