import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com'; // CHANGE THIS. Use your bsky handle. Should be YOURNAME.bsky.social, unless you have a custom domain like me.
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz'; // CHANGE THIS. It's STRONGLY recommended that you use an App Password created through the Bsky App rather than your own.
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const agent = new BskyAgent({ service: SERVICE_URL });
        const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
        await agent.login({ identifier: did, password: APP_PASSWORD });
        const { data } = await agent.getAuthorFeed({
          actor: did,
          filter: 'posts_and_author_threads',
          limit: 1, // You can set the limit of posts here, up to 100.
        });

        console.log('Received data:', data); // Log received data

        // Check if data.feed is an array before mapping
        if (Array.isArray(data.feed)) {
          const postsData = data.feed.map(item => {
            // Here's how a Banger Score is tallied: Likes = 200pts, Comments = 300pts, Reskeets = 1000pts
            const bangerScoreForPost = (item.post.likeCount * 200) + (item.post.replyCount * 300) + (item.post.repostCount * 1000);

            // Format Bluesky users mentioned in the post
            let formattedText = item.post.record.text;
            const mentionPattern = /@([^\s]+)/g;
            formattedText = formattedText.replace(mentionPattern, '<a href="https://bsky.app/profile/$1" target="_blank">@$1</a>');

            // Replace YouTube links with embedded player
            const youtubePattern = /https:\/\/youtu\.be\/([^\s]+)/g;
            formattedText = formattedText.replace(youtubePattern, '<iframe width="560" height="315" src="https://www.youtube.com/embed/$1" frameborder="0" allowfullscreen></iframe>');

            return {
              date: new Date(item.post.record.createdAt).toLocaleString('en-US', {
                timeZone: 'America/New_York', // CHANGE THIS: Adjust to your timezone
                dateStyle: 'long',
                timeStyle: 'long'
              }),
              text: formattedText,
              bangerScore: bangerScoreForPost,
              images: item.post.embed?.images?.map(image => ({
                thumb: image.thumb,
                fullsize: image.fullsize,
                alt: image.alt
              })) || [] // Handle cases where images are not present
            };
          });
          setPosts(postsData);
        } else {
          console.error('Invalid feed data format:', data.feed);
          throw new Error('Invalid feed data format');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  if (error) {
    return <div className='bsky-home skeet-text'>BlueSky Error: {error}</div>;
  }

  return (
    <section className='bsky-home'>
      <h2>Latest Skeet To Bsky</h2>
      <ul className='latest-skeet'>
        {posts.map((post, index) => (
          <li key={index}>
            <p className='skeet-date'>Posted by <a href='https://bsky.app/profile/reverendcrush.com' target="_blank" rel="noreferrer">@reverendcrush.com</a> to <a href='https://bsky.app/' target="_blank" rel="noreferrer">bsky.app</a> on <u>{post.date}</u></p>
            <div className='skeet-text' style={{ whiteSpace: 'pre-line' }} dangerouslySetInnerHTML={{ __html: post.text }}></div>
            <div className='skeet-image-group'>
              {post.images.map((image, i) => (
                <div key={i} className="skeet-image"><img className='skeet-img-file' key={i} src={image.thumb} alt={image.alt} /></div>
              ))}
            </div>
            <h3>Skeet Banger Score: {post.bangerScore}</h3>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default BlueskySocial;
