// Import the necessary packages
import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';
import { MetaTags } from 'react-meta-tags';
import YouTube from 'react-youtube';

// Define your component
const BlueskySocial = () => {
  // Define your Bluesky handle, app password, and service URL
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz';
  const SERVICE_URL = 'https://bsky.social';

  // State to store posts and error
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  // Fetch data from Bluesky
  useEffect(() => {
    async function fetchData() {
      try {
        const agent = new BskyAgent({ service: SERVICE_URL });
        const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
        await agent.login({ identifier: did, password: APP_PASSWORD });
        const { data } = await agent.getAuthorFeed({
          actor: did,
          filter: 'posts_and_author_threads',
          limit: 1,
        });

        // Process received data
        if (Array.isArray(data.feed)) {
          const postsData = data.feed.map(item => {
            let formattedText = item.post.record.text.replace(
              /@([^\s]+)/g,
              '<a href="https://bsky.app/profile/$1" target="_blank">@$1</a>'
            );

            return {
              date: new Date(item.post.record.createdAt).toLocaleString('en-US', {
                timeZone: 'America/New_York',
                dateStyle: 'long',
                timeStyle: 'long'
              }),
              text: formattedText,
            };
          });
          setPosts(postsData);
        } else {
          console.error('Invalid feed data format:', data.feed);
          throw new Error('Invalid feed data format');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  // Render the component
  if (error) {
    return <div className='bsky-home skeet-text'>BlueSky Error: {error}</div>;
  }

  return (
    <section className='bsky-home'>
      <MetaTags>
        <title>Bluesky Social</title>
        <meta name="description" content="Bluesky Social Feed" />
      </MetaTags>
      <h2>Latest Skeet To Bsky</h2>
      <ul className='latest-skeet'>
        {posts.map((post, index) => (
          <li key={index}>
            <p className='skeet-date'>Posted by <a href={`https://bsky.app/profile/${HANDLE}`} target="_blank" rel="noreferrer">@{HANDLE}</a> to <a href='https://bsky.app/' target="_blank" rel="noreferrer">bsky.app</a> on <u>{post.date}</u></p>
            <div className='skeet-text' style={{ whiteSpace: 'pre-line' }}>
              <div dangerouslySetInnerHTML={{ __html: post.text }}></div>
              {post.text.includes('youtu.be') && <YouTube videoId={getPostYoutubeId(post.text)} />}
            </div>
          </li>
        ))}
      </ul>
    </section>
  );
};

// Function to extract YouTube video ID from the post text
const getPostYoutubeId = (text) => {
  const matches = text.match(/youtu\.be\/([^?\s]+)/);
  return matches ? matches[1] : null;
};

export default BlueskySocial;
