import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const PASSWORD = 'no6e-unlo-oob2-exqz'; // Replace with your actual password
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const agent = new BskyAgent({ service: SERVICE_URL });
        await agent.login({ identifier: HANDLE, password: PASSWORD });

        // Once authenticated, proceed to fetch the latest posts
        const latestPosts = await agent.getLatestPosts({ handle: HANDLE, limit: 10 });
        setPosts(latestPosts);
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Latest Posts</h1>
      <ul>
        {posts.map((post, index) => (
          <li key={index}>
            <p>Date: {post.date}</p>
            <p>Text: {post.text}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BlueskySocial;
