import React, { useEffect, useState } from 'react';
import { BskyAgent } from '@atproto/api';
import YouTube from 'react-youtube';

const BlueskySocial = () => {
  const HANDLE = 'reverendcrush.com';
  const APP_PASSWORD = 'no6e-unlo-oob2-exqz';
  const SERVICE_URL = 'https://bsky.social';

  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    async function fetchData() {
      try {
        const agent = new BskyAgent({ service: SERVICE_URL });
        const { data: { did } } = await agent.resolveHandle({ handle: HANDLE });
        await agent.login({ identifier: did, password: APP_PASSWORD });
        const { data } = await agent.getAuthorFeed({
          actor: did,
          filter: 'posts_and_author_threads',
          limit: 1,
        });
        
        if (Array.isArray(data.feed)) {
          const postsData = data.feed.map(item => {
            const post = item.post;
            const { likeCount, replyCount, repostCount } = post;
            const bangerScore = likeCount * 200 + replyCount * 300 + repostCount * 1000;
            const images = post.embed?.media?.images || [];
            console.log(post)
            // Debugging original post images
            if (post.embed && post.embed.media && post.embed.media.images) {
              console.log("Original Post Images Data:", post.embed.media.images);
              post.embed.media.images.forEach((image, imgIndex) => {
                if (image && image.fullsize) {
                  console.log("Original Post Image URL:", image.fullsize);
                } else {
                  console.log(`Missing image data for original post image [${imgIndex}]`);
                }
              });
            }
            return {
              author: post.author,
              date: new Date(post.record.createdAt).toLocaleString('en-US', { timeZone: 'America/New_York', dateStyle: 'long', timeStyle: 'long' }),
              text: post.record.text,
              embed: post.embed,
              bangerScore: bangerScore,
              images: images
            };
          });
          setPosts(postsData);
        } else {
          console.error('Invalid feed data format:', data.feed);
          throw new Error('Invalid feed data format');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      }
    }

    fetchData();
  }, []);

  useEffect(() => {
    console.log('Posts State Updated:', posts);
    posts.forEach((post, index) => {
      console.log(`Post [${index}]:`, post);
      if (post.embed && post.embed.media && post.embed.media.images) {
        console.log(`Original Post Images Data [${index}]:`, post.embed.media.images);
        post.embed.media.images.forEach((image, imgIndex) => {
          if (image && image.fullsize) {
            console.log(`Original Post Image [${imgIndex}] URL:`, image.fullsize);
          } else {
            console.log(`Missing image data for original post image [${imgIndex}]`);
          }
        });
      }

      // Adding console.log to inspect quoted user's images
      if (post.embed && post.embed.record && post.embed.record.record && post.embed.record.record.value && post.embed.record.record.value.embed && post.embed.record.record.value.embed.images) {
        console.log(`Quoted Post Images Data [${index}]:`, post.embed.record.record.value.embed.images);
        post.embed.record.record.value.embed.images.forEach((image, imgIndex) => {
          console.log(`Quoted Post Image [${imgIndex}] Data:`, image);
          if (image.image && image.image.ref && image.image.ref['$link']) {
            console.log(`Quoted Post Image [${imgIndex}] URL:`, `https://cdn.bsky.app/img/feed_fullsize/plain/${image.image.ref['$link']}@jpeg`);
          } else {
            console.log(`Missing image data for quoted post image [${imgIndex}]`);
          }
        });
      }
    });
  }, [posts]);

  // Render function for images
  const renderImages = (images) => {
    return images.map((image, imgIndex) => (
      <img key={imgIndex} src={image.fullsize} alt="" className='skeet-image-file' />
    ));
  };

  if (error) {
    return <div className='bsky-home skeet-text'>BlueSky Error: {error}</div>;
  }

  return (
    <section className='bsky-home'>
      <h2>Latest Skeet On Bsky</h2>
      <ul className='latest-skeet'>
      
      {posts.map((post, index) => {
        // Extracting images from the original post
        const originalPostImages = post.embed && post.embed.media && post.embed.media.images ? post.embed.media.images : [];

        // Attempting to extract images from the quoted post
        let quotedPostImages = [];
        if (post.embed && post.embed.record && post.embed.record.record && post.embed.record.record.value && post.embed.record.record.value.embed && post.embed.record.record.value.embed.images) {
          quotedPostImages = post.embed.record.record.value.embed.images;
        }

        return (
          <li key={index}>
            <p className='skeet-date'>Posted by <a href={`https://bsky.app/profile/${post.author.handle}`} target="_blank" rel="noreferrer">@{post.author.handle}</a> on <u>{post.date}</u></p>
            <div className='skeet-text' style={{ whiteSpace: 'pre-line' }}>
              {parseText(post.text)}
              {/* Render images from the original post */}
              <div className="skeet-image-group">
                {renderImages(originalPostImages)}
              </div>
              {/* Render images from the quoted post, if any */}
              <div className="skeet-image-group">
                {renderImages(quotedPostImages)}
              </div>
              {post.embed && post.embed.external && post.embed.external.uri.includes('youtu.be') && (
                <div className='skeet-youtube'>
                  <YouTube videoId={getPostYoutubeId(post.embed.external.uri)} />
                  <h3>{post.embed.external.title}</h3>
                  <p className='skeet-metatext'>{post.embed.external.description}</p>
                </div>
              )}
            </div>
            {/* Continue with any additional post rendering logic */}
            <h3 className='skeet-banger-score'>Banger Score:<br /> <span className='glow'>{post.bangerScore}</span></h3>
          </li>
        );
      })}
      </ul>
    </section>
  );
};

const constructImageUrl = (did, blobRef) => {
  return `https://cdn.bsky.app/img/feed_thumbnail/plain/${did}/${blobRef}@jpeg`;
};

const parseText = (text) => {
  if (!text) return ''; // Return an empty string if text is undefined
  const usernameRegex = /@(\w+(?:\.\w+)*)(?=[^\w.@]|$)/g;

  return text.split(usernameRegex).map((part, index) => {
    if (index % 2 === 0) {
      return part; // Normal text
    } else {
      return <a href={`https://bsky.app/profile/${part}`} target="_blank" rel="noreferrer">@{part}</a>; // Link to user profile
    }
  });
};

const getPostYoutubeId = (text) => {
  const matches = text.match(/youtu\.be\/([^?\s]+)/);
  return matches ? matches[1] : null;
};

export default BlueskySocial;
