export function assertValidXrpcParams(
  lexicons: Lexicons,
  def: LexXrpcProcedure | LexXrpcQuery | LexXrpcSubscription,
  value: unknown,
) {
  if (def.parameters) {
    const res = XrpcValidators.params(lexicons, 'Params', def.parameters, value)
    if (!res.success) throw res.error
    return res.value
  }
