import React, { useRef, useEffect} from 'react';
import {Link, useLocation} from 'react-router-dom';
import { useTyped } from '../contexts/TypedContext';
import Typed from "typed.js";
import "../styles/Header.css";
import {pushRotate as Menu} from 'react-burger-menu';
import logo from '../img/dudeist_yinyang.png';

export default function Header(){
    const el = useRef(null);
    const { setTyped } = useTyped(); // Ref for storing the Typed instance
    let location = useLocation();

    useEffect(() => {
        const stringsMap = {
            "/about": ">> REVCRSH_FUN: Fun stuff that the Rev cobbled together.",
            "/projects": ">> REVCRSH_BIO: A little about about me, Dr. Reverend Crush!",
            "/contact": ">> REVCRSH_CON: Ways to contact His Royal Dudeness other than bsky.",
            "/": ">> REVCRSH_HOM: Hey Duders! Enjoy your visit!",
        };
        const defaultString = ">> //ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R ERR0R";
        const strings = [stringsMap[location.pathname] || defaultString];

        const typed = new Typed(el.current, {
            strings: strings,
            typeSpeed: 50,
        });

        setTyped(typed);

        return () => {
            typed.destroy();
        };
    }, [location.pathname]); // Re-run effect when location changes

    return (
    <div className="header">
        <div className="autotext" ref={el}></div>
        <div className="header_nav">
        <Menu right noOverlay width={ '29.5%' } height={ '100%' } pageWrapId={"page-wrap"} outerContainerId={"outer-container"}>
            <img className="nav-logo" src={logo} alt='ReverendCrush.com' />
            <Link className="nav_link" onClick={killTyped} to='/'>Main </Link>
            <Link className="nav_link" onClick={killTyped} to='/contact'>Socials</Link>
            <Link className="nav_link" onClick={killTyped} to='/projects'>For Fun</Link>
            <Link className="nav_link" onClick={killTyped} to='/about'>Bio</Link>
        </Menu>
        </div>
    </div>      
        );
}
